import React, { useState } from "react";
import {
	Form,
	FormGroup,
	Input,
	InputGroup,
	InputGroupAddon,
	Button,
	Container,
} from "reactstrap";

import { v4 } from "uuid";

const TodoForm = ({ addTodos }) => {
	const [todoString, setTodoString] = useState("");

	const handleSubmit = (e) => {
		e.preventDefault();
		if (todoString === "") {
			return alert("Please Enter Some Task");
		}
		const todo = {
			title: todoString,
			id: v4(),
		};
		addTodos(todo);
		setTodoString("");
	};

	return (
		<Form onSubmit={handleSubmit}>
			<FormGroup>
				<InputGroup>
					<Input
						type="text"
						name="todo"
						id="todo"
						placeholder="Enter A ToDo"
						value={todoString}
						onChange={(e) => setTodoString(e.target.value)}
					></Input>
					<InputGroupAddon addonType="a">
						<Button color="success">Add To Do</Button>
					</InputGroupAddon>
				</InputGroup>
			</FormGroup>
		</Form>
	);
};

export default TodoForm;
